mapper.update:
update t_common_commodity
set status = #{status},
operator = #{operator}
where id = #{id}

mapper.select:
select tc.id, tc.commdity_name, tc.type_code, tc.style_code, t.name typeName, tcc.name styleName, tc.marke_price, tc.discount_price, tc.special_id,tc.price from t_common_commodity tc left join t_category_config t on tc.type_code = t.id left join t_category_config tcc on tc.style_code = tcc.id where tc.special_id in
#{id}
and tc.status = 0

select

from t_common_commodity
where status = 0

    and special_id = #{specialId}


    and style_code in

        #{id}



    and style_code = #{styleCode}


    and type_code = #{typeCode}


    and price >= #{minPrice}


    and price < #{maxPrice}


    and is_recommend = #{isRecommend}



        and stock > 0


        and stock = 0


 order by


        price desc,


        price asc,


create_time desc

mapper.update:
update t_common_commodity
set stock = #{stock},
update_time = #{updateTime}
where id = #{id}

UPDATE t_common_commodity a
  SET a.interest_count = IFNULL(a.interest_count,0) + #{interestCount}
WHERE a.id = #{id}
